mapper.select:
select
id,energy_consumption,project_type,time_type,energy_consumption_num,warning_value
from t_carbon_emission


        and id = #{id}


        and energy_consumption = #{energyConsumption}


        and project_type = #{projectType}


        and time_type = #{timeType}


        and energy_consumption_num = #{energyConsumptionNum}


        and warning_value = #{warningValue}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_carbon_emission


        and id = #{id}


        and energy_consumption = #{energyConsumption}


        and project_type = #{projectType}


        and time_type = #{timeType}


        and energy_consumption_num = #{energyConsumptionNum}


        and warning_value = #{warningValue}
